import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CustomerRegister = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();
    // Simulate customer registration
    console.log('Customer registered with email:', email);
    localStorage.setItem('customerLoggedIn', 'true');
    navigate('/');
  };

  return (
    <div style={{ maxWidth: '400px', margin: '80px auto', padding: '2rem', textAlign: 'center', fontFamily: 'Arial, sans-serif' }}>
      <h2>Customer Registration</h2>
      <form onSubmit={handleRegister} style={{ textAlign: 'left' }}>
        <div style={{ marginBottom: '1rem' }}>
          <label style={{ display: 'block', fontWeight: 'bold', marginBottom: '0.5rem' }}>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label style={{ display: 'block', fontWeight: 'bold', marginBottom: '0.5rem' }}>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>
        <button
          type="submit"
          style={{ width: '100%', padding: '0.75rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default CustomerRegister;
