import React, { useState, useEffect } from 'react';

const VendorPortal = () => {
  // Retrieve store data from localStorage (from the registration page)
  const savedStoreData = localStorage.getItem('storeData');
  const storeData = savedStoreData ? JSON.parse(savedStoreData) : null;

  // Local state for vendor's item listings
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    imageUrl: '',
    // We'll use the store's location for the item details; however, on the public home page it will remain hidden.
    location: storeData ? storeData.location : ''
  });

  const handleNewItemChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleNewItemSubmit = (e) => {
    e.preventDefault();
    // Create a new item with a unique id (for prototype, using current timestamp)
    const itemToAdd = { ...newItem, id: Date.now(), shop: storeData ? storeData.storeName : 'Unknown' };
    setItems([...items, itemToAdd]);
    // Optionally, you could persist this to localStorage or a global state so that Home page reflects it.
    setNewItem({ name: '', description: '', imageUrl: '', location: storeData ? storeData.location : '' });
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>Vendor Portal</h2>
      {storeData ? (
        <div style={{ marginBottom: '2rem' }}>
          <h3>{storeData.storeName}</h3>
          <p>{storeData.description}</p>
          <p><strong>Location:</strong> {storeData.location}</p>
        </div>
      ) : (
        <p>No store data found. Please register your store.</p>
      )}
      
      <h3>Add a New Item</h3>
      <form onSubmit={handleNewItemSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
        <div style={{ marginBottom: '1rem' }}>
          <label>Item Name:</label><br/>
          <input type="text" name="name" value={newItem.name} onChange={handleNewItemChange} required />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label>Description:</label><br/>
          <textarea name="description" value={newItem.description} onChange={handleNewItemChange} required />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label>Image URL:</label><br/>
          <input type="text" name="imageUrl" value={newItem.imageUrl} onChange={handleNewItemChange} required />
        </div>
        <button type="submit" style={{ padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Add Item
        </button>
      </form>

      {/* List the items added by the vendor */}
      <div style={{ marginTop: '2rem' }}>
        <h3>Your Items</h3>
        {items.length === 0 ? (
          <p>No items added yet.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {items.map(item => (
              <li key={item.id} style={{ marginBottom: '1rem', border: '1px solid #ddd', padding: '1rem', borderRadius: '8px' }}>
                <h4>{item.name}</h4>
                <p>{item.description}</p>
                <p><strong>Image URL:</strong> {item.imageUrl}</p>
                {/* The location is stored with the item but on the public Home page, it will be hidden */}
                <p><strong>Location:</strong> {item.location}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default VendorPortal;
