import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RegisterStore = () => {
  const navigate = useNavigate();
  const [storeName, setStoreName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // For now, just log the store name and navigate to the vendor portal.
    console.log('Registering store:', storeName);
    navigate('/vendor');
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>Register Your Store</h2>
      <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
        <div style={{ marginBottom: '1rem' }}>
          <label>Store Name:</label><br/>
          <input
            type="text"
            value={storeName}
            onChange={(e) => setStoreName(e.target.value)}
            required
          />
        </div>
        <button type="submit" style={{ padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Register Store
        </button>
      </form>
    </div>
  );
};

export default RegisterStore;
