import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ShopCard from '../components/ShopCard';

const dummyShops = [
  { id: 1, name: 'Thrift Haven', description: 'Great deals on second-hand finds', quadrant: 'North', imageUrl: '/images/shop1.jpg' },
  { id: 2, name: 'Op Shop Express', description: 'Quick service and quality items', quadrant: 'South', imageUrl: '/images/shop2.jpg' },
  // Add more dummy shops as needed
];

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedQuadrant, setSelectedQuadrant] = useState('All');

  const filteredShops = dummyShops.filter(shop => {
    const matchesSearch = shop.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesQuadrant = selectedQuadrant === 'All' || shop.quadrant === selectedQuadrant;
    return matchesSearch && matchesQuadrant;
  });

  return (
    <div className="home-page" style={{ padding: '1rem' }}>
      <h1>Welcome to oppy</h1>
      
      {/* Authentication and Store Registration Options */}
      <div className="auth-options" style={{ marginBottom: '1rem' }}>
        <Link to="/login/customer">
          <button style={{ marginRight: '1rem', padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px' }}>
            Login as Customer
          </button>
        </Link>
        <Link to="/login/vendor">
          <button style={{ marginRight: '1rem', padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px' }}>
            Login as Vendor
          </button>
        </Link>
        <Link to="/register/store">
          <button style={{ padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px' }}>
            Register as Store
          </button>
        </Link>
      </div>
      
      {/* Search and Filter Section */}
      <div className="search-filter" style={{ marginBottom: '2rem' }}>
        <input
          type="text"
          placeholder="Search for shops..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '0.5rem', marginRight: '1rem' }}
        />
        <div className="quadrant-filter" style={{ display: 'inline-block' }}>
          {['All', 'North', 'South', 'East', 'West', 'Central'].map((q) => (
            <button
              key={q}
              onClick={() => setSelectedQuadrant(q)}
              style={{
                backgroundColor: selectedQuadrant === q ? '#ffa24d' : '#fff',
                color: selectedQuadrant === q ? '#fff' : '#333',
                margin: '0 0.5rem',
                padding: '0.5rem 1rem',
                border: '1px solid #ffa24d',
                borderRadius: '4px'
              }}
            >
              {q}
            </button>
          ))}
        </div>
      </div>
      
      {/* Shop Listings */}
      <div className="shop-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {filteredShops.map(shop => (
          <ShopCard key={shop.id} shop={shop} />
        ))}
      </div>
    </div>
  );
};

export default Home;
