import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ItemCard from '../components/ItemCard';
import logo from '../assets/logo.png';

// Dummy items data with quadrant field added
const dummyItems = [
  { 
    id: 1, 
    name: 'Vintage Chair', 
    description: 'A stylish vintage chair for your living room.', 
    shop: 'Thrift Haven', 
    location: '123 Main St, CityName', 
    imageUrl: '/images/chair.jpg',
    quadrant: 'Central'
  },
  { 
    id: 2, 
    name: 'Antique Lamp', 
    description: 'An elegant antique lamp to light up your space.', 
    shop: 'Op Shop Express', 
    location: '456 Side St, CityName', 
    imageUrl: '/images/lamp.jpg',
    quadrant: 'North'
  },
  // Add more dummy items as needed
];

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedQuadrant, setSelectedQuadrant] = useState('All');
  const [vendorItems, setVendorItems] = useState([]);

  // On component mount, load vendor items from localStorage
  useEffect(() => {
    const storedVendorItems = localStorage.getItem('vendorItems');
    if (storedVendorItems) {
      setVendorItems(JSON.parse(storedVendorItems));
    }
  }, []);

  // Combine dummy items and vendor items
  const allItems = [...dummyItems, ...vendorItems];

  // Filter items by name/description and quadrant
  const filteredItems = allItems.filter(item => {
    const lowerSearch = searchTerm.toLowerCase();
    const matchesSearch = item.name.toLowerCase().includes(lowerSearch) || item.description.toLowerCase().includes(lowerSearch);
    const matchesQuadrant = selectedQuadrant === 'All' || item.quadrant === selectedQuadrant;
    return matchesSearch && matchesQuadrant;
  });

  return (
    <div className="home-page" style={{ textAlign: 'center', padding: '2rem' }}>
      {/* Display the enlarged logo */}
      <img src={logo} alt="oppey logo" style={{ height: '300px', marginBottom: '1rem' }} />

      {/* Search and Quadrant Filter */}
      <div className="search-filter" style={{ marginBottom: '2rem', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '1rem' }}>
        <input
          type="text"
          placeholder="Search for items..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '0.5rem', maxWidth: '300px', width: '100%' }}
        />
        <div className="quadrant-filter" style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <label style={{ fontWeight: 'bold' }}>Filter by Quadrant:</label>
          <select
            value={selectedQuadrant}
            onChange={(e) => setSelectedQuadrant(e.target.value)}
            style={{ padding: '0.5rem', borderRadius: '4px' }}
          >
            <option value="All">All</option>
            <option value="Central">Central</option>
            <option value="North">North</option>
            <option value="South">South</option>
            <option value="East">East</option>
            <option value="West">West</option>
          </select>
        </div>
      </div>

      {/* Item Listings */}
      <div className="item-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '1rem' }}>
        {filteredItems.map(item => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
};

export default Home;
