import React from 'react';
import { Link } from 'react-router-dom';

const ShopCard = ({ shop }) => {
  return (
    <div className="shop-card" style={{ border: '1px solid #ddd', padding: '1rem', margin: '1rem', width: '300px' }}>
      <img src={shop.imageUrl} alt={shop.name} style={{ width: '100%' }} />
      <h3>{shop.name}</h3>
      <p>{shop.description}</p>
      <p><strong>Quadrant:</strong> {shop.quadrant}</p>
      <Link to={`/shops/${shop.id}`}>View Details</Link>
    </div>
  );
};

export default ShopCard;
