import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ShopCard from '../components/ShopCard';
import logo from '../assets/logo.png'; // Ensure your logo image is in this folder

const dummyShops = [
  { id: 1, name: 'Thrift Haven', description: 'Great deals on second-hand finds', quadrant: 'North', imageUrl: '/images/shop1.jpg' },
  { id: 2, name: 'Op Shop Express', description: 'Quick service and quality items', quadrant: 'South', imageUrl: '/images/shop2.jpg' },
  // Add more dummy shops as needed
];

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedQuadrant, setSelectedQuadrant] = useState('All');

  const filteredShops = dummyShops.filter(shop => {
    const matchesSearch = shop.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesQuadrant = selectedQuadrant === 'All' || shop.quadrant === selectedQuadrant;
    return matchesSearch && matchesQuadrant;
  });

  return (
    <div className="home-page" style={{ textAlign: 'center', padding: '2rem' }}>
      {/* Enlarged logo image */}
      <img 
        src={logo} 
        alt="oppey logo" 
        style={{ height: '300px', marginBottom: '1rem' }} 
      />

      {/* Authentication and Store Registration Options */}
      <div className="auth-options" style={{ marginBottom: '1.5rem' }}>
        <Link to="/login/customer">
          <button>Login as Customer</button>
        </Link>
        <Link to="/login/vendor">
          <button>Login as Vendor</button>
        </Link>
        <Link to="/register/store">
          <button>Register as Store</button>
        </Link>
      </div>
      
      {/* Search and Filter Section */}
      <div className="search-filter" style={{ marginBottom: '2rem', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '1rem' }}>
        <input
          type="text"
          placeholder="Search for shops..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '0.5rem', maxWidth: '300px', width: '100%' }}
        />
        <div className="quadrant-filter" style={{ display: 'flex', justifyContent: 'center', gap: '0.5rem' }}>
          {['All', 'North', 'South', 'East', 'West', 'Central'].map((q) => (
            <button
              key={q}
              onClick={() => setSelectedQuadrant(q)}
            >
              {q}
            </button>
          ))}
        </div>
      </div>
      
      {/* Shop Listings */}
      <div className="shop-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '1rem' }}>
        {filteredShops.map(shop => (
          <ShopCard key={shop.id} shop={shop} />
        ))}
      </div>
    </div>
  );
};

export default Home;
