import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/VendorLogin.css';

const VendorLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // For demonstration, assume the vendor login credentials are:
    // Email: vendor@example.com and password: password
    if (email === 'vendor@example.com' && password === 'password') {
      localStorage.setItem('vendorLoggedIn', 'true');
      localStorage.setItem('vendorStoreName', 'Vendor Shop');
      navigate('/vendor');
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  const handleSocialLogin = (provider) => {
    // Simulate social login
    localStorage.setItem('vendorLoggedIn', 'true');
    localStorage.setItem('vendorStoreName', 'Vendor Shop');
    navigate('/vendor');
  };

  return (
    <div className="vendor-login">
      <h2>Vendor Login</h2>
      {error && <p className="vendor-login__error">{error}</p>}
      <form onSubmit={handleLogin} className="vendor-login__form">
        <div className="vendor-login__form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="vendor-login__form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="vendor-login__button">
          Login
        </button>
      </form>
      <div className="vendor-login__social">
        <p>Or log in with:</p>
        <button
          className="vendor-login__social-button"
          onClick={() => handleSocialLogin('Google')}
        >
          Google
        </button>
        <button
          className="vendor-login__social-button"
          onClick={() => handleSocialLogin('Facebook')}
        >
          Facebook
        </button>
      </div>
    </div>
  );
};

export default VendorLogin;
