import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ItemCard from '../components/ItemCard';
import logo from '../assets/logo.png'; // Make sure your logo image is in src/assets/

// Dummy items data (each item belongs to a shop)
const dummyItems = [
  { id: 1, name: 'Vintage Chair', description: 'A stylish vintage chair for your living room.', shop: 'Thrift Haven', imageUrl: '/images/chair.jpg' },
  { id: 2, name: 'Antique Lamp', description: 'An elegant antique lamp to light up your space.', shop: 'Op Shop Express', imageUrl: '/images/lamp.jpg' },
  // Add more dummy items as needed
];

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');

  // Filter items by name or description based on the search term
  const filteredItems = dummyItems.filter(item => {
    const lowerSearch = searchTerm.toLowerCase();
    return item.name.toLowerCase().includes(lowerSearch) || item.description.toLowerCase().includes(lowerSearch);
  });

  return (
    <div className="home-page" style={{ textAlign: 'center', padding: '2rem' }}>
      {/* Display the enlarged logo */}
      <img src={logo} alt="oppey logo" style={{ height: '300px', marginBottom: '1rem' }} />
      
      {/* Authentication and Store Registration Options */}
      <div className="auth-options" style={{ marginBottom: '1.5rem' }}>
        <Link to="/login/customer"><button>Login as Customer</button></Link>
        <Link to="/login/vendor"><button>Login as Vendor</button></Link>
        <Link to="/register/store"><button>Register as Store</button></Link>
      </div>
      
      {/* Search Section for Items */}
      <div className="search-filter" style={{ marginBottom: '2rem', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '1rem' }}>
        <input
          type="text"
          placeholder="Search for items..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '0.5rem', maxWidth: '300px', width: '100%' }}
        />
      </div>
      
      {/* Item Listings */}
      <div className="item-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '1rem' }}>
        {filteredItems.map(item => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
};

export default Home;
