import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RegisterStore = () => {
  const navigate = useNavigate();
  const [storeData, setStoreData] = useState({
    storeName: '',
    description: '',
    location: '',
    email: '',
    phone: ''
  });
  
  // Track if the registration was submitted
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStoreData({ ...storeData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Log the store data (simulate saving to a database)
    console.log('Store registration submitted:', storeData);

    // Simulate sending automated email (in a real app, use backend email API)
    console.log(`Email sent to ${storeData.email} and management: Store registration submission received.`);

    // Save storeData in localStorage for later use (optional)
    localStorage.setItem('storeData', JSON.stringify(storeData));

    // Set submitted state to true to display the confirmation message
    setSubmitted(true);

    // Optionally, you can redirect to a different page after a delay
    // setTimeout(() => navigate('/vendor'), 3000);
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      {submitted ? (
        <div>
          <h2>We will get back to you</h2>
          <p>Your store registration has been submitted. An automated email has been sent regarding your submission.</p>
        </div>
      ) : (
        <>
          <h2>Register Your Store</h2>
          <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
            <div style={{ marginBottom: '1rem' }}>
              <label>Store Name:</label><br />
              <input
                type="text"
                name="storeName"
                value={storeData.storeName}
                onChange={handleChange}
                required
              />
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <label>Description:</label><br />
              <textarea
                name="description"
                value={storeData.description}
                onChange={handleChange}
                required
              />
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <label>Location:</label><br />
              <input
                type="text"
                name="location"
                value={storeData.location}
                onChange={handleChange}
                required
              />
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <label>Email:</label><br />
              <input
                type="email"
                name="email"
                value={storeData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <label>Phone:</label><br />
              <input
                type="text"
                name="phone"
                value={storeData.phone}
                onChange={handleChange}
                required
              />
            </div>
            <button
              type="submit"
              style={{
                padding: '0.5rem 1rem',
                backgroundColor: '#ffa24d',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Submit Registration
            </button>
          </form>
        </>
      )}
    </div>
  );
};

export default RegisterStore;
