import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/CustomerLogin.css';

const CustomerLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleTraditionalLogin = (e) => {
    e.preventDefault();
    // For the prototype, simply log the email and "log in"
    console.log('Logging in with:', email);
    // You could store a flag in localStorage to simulate a logged-in customer.
    localStorage.setItem('customerLoggedIn', 'true');
    navigate('/');
  };

  const handleSocialLogin = (provider) => {
    // In a real app, you'd call your OAuth flow for the provider.
    console.log(`Logging in with ${provider}`);
    localStorage.setItem('customerLoggedIn', 'true');
    navigate('/');
  };

  return (
    <div className="customer-login">
      <h2>Customer Login</h2>
      <form onSubmit={handleTraditionalLogin} className="customer-login__form">
        <div className="customer-login__form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="customer-login__form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="customer-login__button">
          Login
        </button>
      </form>

      <div className="customer-login__social">
        <p>Or log in with:</p>
        <button onClick={() => handleSocialLogin('Google')} className="customer-login__social-button">
          Google
        </button>
        <button onClick={() => handleSocialLogin('Facebook')} className="customer-login__social-button">
          Facebook
        </button>
        {/* You can add more social buttons as needed */}
      </div>

      <p>
        New customer? <a href="/register/customer">Register here</a>
      </p>
    </div>
  );
};

export default CustomerLogin;
