import React, { useState } from 'react';

const VendorPortal = () => {
  // Retrieve vendor store name from localStorage
  const vendorStoreName = localStorage.getItem('vendorStoreName') || 'Your Store';
  
  // State for items listed by the vendor
  const [items, setItems] = useState([]);
  
  // State for new item form
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    imageUrl: '',
    location: '' // Vendor-specified location for the item
  });
  
  // State for simulated analytics: each item ID maps to analytics data
  const [analytics, setAnalytics] = useState({});

  const handleNewItemChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleNewItemSubmit = (e) => {
    e.preventDefault();
    // Create a new item with a unique id
    const itemToAdd = { ...newItem, id: Date.now(), shop: vendorStoreName };
    setItems([...items, itemToAdd]);
    // Initialize analytics for the new item
    setAnalytics({
      ...analytics,
      [itemToAdd.id]: { clicks: 0, unlocks: 0 }
    });
    // Reset the form (optional: preserve the location if desired)
    setNewItem({ name: '', description: '', imageUrl: '', location: newItem.location });
  };

  // Simulate recording a click on an item (e.g., when a customer clicks "View Details")
  const recordClick = (itemId) => {
    setAnalytics({
      ...analytics,
      [itemId]: { ...analytics[itemId], clicks: (analytics[itemId].clicks || 0) + 1 }
    });
  };

  // Simulate a payment for unlocking an item's location
  const recordUnlock = (itemId) => {
    setAnalytics({
      ...analytics,
      [itemId]: { ...analytics[itemId], unlocks: (analytics[itemId].unlocks || 0) + 1 }
    });
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>Vendor Portal</h2>
      <h3>Welcome, {vendorStoreName}</h3>
      <div style={{ marginBottom: '2rem' }}>
        <h4>Dashboard Analytics</h4>
        {items.length === 0 ? (
          <p>No items listed yet.</p>
        ) : (
          items.map(item => (
            <div key={item.id} style={{ border: '1px solid #ddd', padding: '1rem', marginBottom: '1rem' }}>
              <h4>{item.name}</h4>
              <p>{item.description}</p>
              <p><strong>Clicks:</strong> {analytics[item.id]?.clicks || 0}</p>
              <p><strong>Location Unlocks:</strong> {analytics[item.id]?.unlocks || 0}</p>
              <button onClick={() => recordClick(item.id)} style={{ marginRight: '1rem', padding: '0.5rem 1rem' }}>
                Simulate Click
              </button>
              <button onClick={() => recordUnlock(item.id)} style={{ padding: '0.5rem 1rem' }}>
                Simulate Unlock Payment
              </button>
            </div>
          ))
        )}
      </div>
      <h3>Add a New Item</h3>
      <form onSubmit={handleNewItemSubmit} style={{ display: 'inline-block', textAlign: 'left', marginBottom: '2rem' }}>
        <div style={{ marginBottom: '1rem' }}>
          <label>Item Name:</label><br />
          <input type="text" name="name" value={newItem.name} onChange={handleNewItemChange} required />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label>Description:</label><br />
          <textarea name="description" value={newItem.description} onChange={handleNewItemChange} required />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label>Image URL:</label><br />
          <input type="text" name="imageUrl" value={newItem.imageUrl} onChange={handleNewItemChange} required />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label>Location:</label><br />
          <input type="text" name="location" value={newItem.location} onChange={handleNewItemChange} required />
        </div>
        <button type="submit" style={{ padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Add Item
        </button>
      </form>
    </div>
  );
};

export default VendorPortal;
