import React, { useState, useEffect } from 'react';
import '../styles/Cart.css';

const Cart = () => {
  // Load cart items from localStorage; if none exist, start with an empty array.
  const [cartItems, setCartItems] = useState(() => {
    return JSON.parse(localStorage.getItem('cartItems')) || [];
  });
  
  // Subscription state: true means customer is subscribed for unlimited unlocks.
  const [subscription, setSubscription] = useState(() => {
    return localStorage.getItem('subscription') === 'true';
  });

  // Update localStorage whenever cartItems or subscription change.
  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  useEffect(() => {
    localStorage.setItem('subscription', subscription.toString());
  }, [subscription]);

  // Simulated function for unlocking an item (called from a listing)
  const handleUnlock = (item) => {
    // Mark item as unlocked and add to the cart
    const updatedCart = [...cartItems, { ...item, unlocked: true }];
    setCartItems(updatedCart);
  };

  // Simulated function for holding an item
  const handleHoldItem = (itemId) => {
    const updatedCart = cartItems.map(item =>
      item.id === itemId ? { ...item, held: true } : item
    );
    setCartItems(updatedCart);
  };

  // Simulated payment details function
  const handlePaymentDetails = () => {
    alert("Payment details have been submitted. Proceeding to checkout...");
  };

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      
      <div className="cart-subscription">
        <label>
          <input
            type="checkbox"
            checked={subscription}
            onChange={() => setSubscription(!subscription)}
          />
          Subscribe for $2.00/month for unlimited unlocks
        </label>
      </div>
      
      <div className="cart-items">
        {cartItems.length === 0 ? (
          <p>Your cart is empty. Click on a listing to unlock it!</p>
        ) : (
          cartItems.map(item => (
            <div key={item.id} className="cart-item">
              {item.imageUrl && (
                <img src={item.imageUrl} alt={item.name} />
              )}
              <div className="cart-item-details">
                <h4>{item.name}</h4>
                <p>{item.description}</p>
                <p><strong>Price:</strong> {item.price}</p>
                <p><strong>Quadrant:</strong> {item.quadrant}</p>
                <p><strong>Location:</strong> {item.location}</p>
                {item.held ? (
                  <p className="held">Item Held</p>
                ) : (
                  <button onClick={() => handleHoldItem(item.id)}>Hold Item</button>
                )}
              </div>
            </div>
          ))
        )}
      </div>
      
      <div className="cart-checkout">
        <button onClick={handlePaymentDetails}>Enter Payment Details and Checkout</button>
      </div>
    </div>
  );
};

export default Cart;
