import React, { useState, useEffect } from 'react';

const VendorPortal = () => {
  // Retrieve vendor store name from localStorage (set during login or registration)
  const vendorStoreName = localStorage.getItem('vendorStoreName') || 'Your Store';

  // Load vendor items from localStorage if available
  const initialVendorItems = JSON.parse(localStorage.getItem('vendorItems')) || [];
  const [items, setItems] = useState(initialVendorItems);

  // State for the new item form; pre-populate location if desired
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    imageUrl: '',
    location: '',
    price: '',
    condition: 'New'
  });

  // State for editing an existing item
  const [editingItemId, setEditingItemId] = useState(null);
  const [editingItemData, setEditingItemData] = useState({
    name: '',
    description: '',
    imageUrl: '',
    location: '',
    price: '',
    condition: 'New'
  });

  // Save items to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('vendorItems', JSON.stringify(items));
  }, [items]);

  const handleNewItemChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imageUrl' && files && files[0]) {
      const imageURL = URL.createObjectURL(files[0]);
      setNewItem({ ...newItem, imageUrl: imageURL });
    } else {
      setNewItem({ ...newItem, [name]: value });
    }
  };

  const handleNewItemSubmit = (e) => {
    e.preventDefault();

    // Enforce maximum of 20 items
    if (items.length >= 20) {
      alert("Maximum of 20 items reached. You cannot add more items.");
      return;
    }

    // Create a new item with a unique id and assign an inventory number (sequentially)
    const itemToAdd = {
      ...newItem,
      id: Date.now(),
      shop: vendorStoreName,
      inventoryNumber: items.length + 1
    };

    setItems([...items, itemToAdd]);
    // Reset the form fields (preserving location if desired)
    setNewItem({ name: '', description: '', imageUrl: '', location: newItem.location, price: '', condition: 'New' });
  };

  const handleEditClick = (item) => {
    setEditingItemId(item.id);
    setEditingItemData({
      name: item.name,
      description: item.description,
      imageUrl: item.imageUrl,
      location: item.location,
      price: item.price,
      condition: item.condition
    });
  };

  const handleEditingItemChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imageUrl' && files && files[0]) {
      const imageURL = URL.createObjectURL(files[0]);
      setEditingItemData({ ...editingItemData, imageUrl: imageURL });
    } else {
      setEditingItemData({ ...editingItemData, [name]: value });
    }
  };

  const handleEditSubmit = (e, itemId) => {
    e.preventDefault();
    const updatedItems = items.map(item => {
      if (item.id === itemId) {
        // Keep the same inventoryNumber
        return { ...item, ...editingItemData };
      }
      return item;
    });
    setItems(updatedItems);
    setEditingItemId(null);
  };

  const handleEditCancel = () => {
    setEditingItemId(null);
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>Vendor Portal</h2>
      <h3>Welcome, {vendorStoreName}</h3>
      
      {/* Display list of vendor items */}
      <div style={{ marginBottom: '2rem' }}>
        <h4>Your Items</h4>
        {items.length === 0 ? (
          <p>No items listed yet.</p>
        ) : (
          items.map(item => (
            <div key={item.id} style={{ border: '1px solid #ddd', padding: '1rem', marginBottom: '1rem', borderRadius: '8px' }}>
              {editingItemId === item.id ? (
                // Edit form for the item
                <form onSubmit={(e) => handleEditSubmit(e, item.id)} style={{ textAlign: 'left' }}>
                  <div style={{ marginBottom: '0.5rem' }}>
                    <label>Item Name:</label><br />
                    <input
                      type="text"
                      name="name"
                      value={editingItemData.name}
                      onChange={handleEditingItemChange}
                      required
                    />
                  </div>
                  <div style={{ marginBottom: '0.5rem' }}>
                    <label>Description:</label><br />
                    <textarea
                      name="description"
                      value={editingItemData.description}
                      onChange={handleEditingItemChange}
                      required
                    />
                  </div>
                  <div style={{ marginBottom: '0.5rem' }}>
                    <label>Price:</label><br />
                    <input
                      type="text"
                      name="price"
                      value={editingItemData.price}
                      onChange={handleEditingItemChange}
                      required
                    />
                  </div>
                  <div style={{ marginBottom: '0.5rem' }}>
                    <label>Condition:</label><br />
                    <select
                      name="condition"
                      value={editingItemData.condition}
                      onChange={handleEditingItemChange}
                      required
                    >
                      <option value="New">New</option>
                      <option value="Used">Used</option>
                      <option value="Refurbished">Refurbished</option>
                    </select>
                  </div>
                  <div style={{ marginBottom: '0.5rem' }}>
                    <label>Upload New Image:</label><br />
                    <input
                      type="file"
                      name="imageUrl"
                      accept="image/*"
                      onChange={handleEditingItemChange}
                    />
                  </div>
                  <div style={{ marginBottom: '0.5rem' }}>
                    <label>Location:</label><br />
                    <input
                      type="text"
                      name="location"
                      value={editingItemData.location}
                      onChange={handleEditingItemChange}
                      required
                    />
                  </div>
                  <button type="submit" style={{ marginRight: '1rem', padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                    Save
                  </button>
                  <button type="button" onClick={handleEditCancel} style={{ padding: '0.5rem 1rem', backgroundColor: '#ccc', color: '#333', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                    Cancel
                  </button>
                </form>
              ) : (
                // Display item details with an Edit button
                <>
                  <h4>{item.inventoryNumber}. {item.name}</h4>
                  <p>{item.description}</p>
                  <p><strong>Price:</strong> {item.price}</p>
                  <p><strong>Condition:</strong> {item.condition}</p>
                  {item.imageUrl && (
                    <img src={item.imageUrl} alt={item.name} style={{ width: '100%', maxWidth: '280px', borderRadius: '4px' }} />
                  )}
                  <p><strong>Location:</strong> {item.location}</p>
                  <button
                    onClick={() => handleEditClick(item)}
                    style={{ marginTop: '0.5rem', padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                  >
                    Edit
                  </button>
                </>
              )}
            </div>
          ))
        )}
      </div>
      
      <h3>Add a New Item</h3>
      {items.length < 20 ? (
        <form onSubmit={handleNewItemSubmit} style={{ display: 'inline-block', textAlign: 'left', marginBottom: '2rem' }}>
          <div style={{ marginBottom: '1rem' }}>
            <label>Item Name:</label><br />
            <input 
              type="text" 
              name="name" 
              value={newItem.name} 
              onChange={handleNewItemChange} 
              required 
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>Description:</label><br />
            <textarea 
              name="description" 
              value={newItem.description} 
              onChange={handleNewItemChange} 
              required 
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>Price:</label><br />
            <input 
              type="text" 
              name="price" 
              value={newItem.price} 
              onChange={handleNewItemChange} 
              required 
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>Condition:</label><br />
            <select
              name="condition"
              value={newItem.condition}
              onChange={handleNewItemChange}
              required
            >
              <option value="New">New</option>
              <option value="Used">Used</option>
              <option value="Refurbished">Refurbished</option>
            </select>
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>Upload Image:</label><br />
            <input 
              type="file" 
              name="imageUrl" 
              accept="image/*"
              onChange={handleNewItemChange} 
              required 
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>Location:</label><br />
            <input 
              type="text" 
              name="location" 
              value={newItem.location} 
              onChange={handleNewItemChange} 
              required 
            />
          </div>
          <button 
            type="submit" 
            style={{ padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
          >
            Add Item
          </button>
        </form>
      ) : (
        <p>You have reached the maximum of 20 items.</p>
      )}
    </div>
  );
};

export default VendorPortal;
