import React, { useState, useEffect } from 'react';

const VendorPortal = () => {
  // Retrieve vendor store name from localStorage (set during login or registration)
  const vendorStoreName = localStorage.getItem('vendorStoreName') || 'Your Store';

  // Load vendor items from localStorage if available
  const initialVendorItems = JSON.parse(localStorage.getItem('vendorItems')) || [];
  const [items, setItems] = useState(initialVendorItems);

  // State for the new item form; pre-populate location if desired
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    imageUrl: '',
    location: '',
    price: '',
    condition: 'New'
  });

  // State for editing an existing item
  const [editingItemId, setEditingItemId] = useState(null);
  const [editingItemData, setEditingItemData] = useState({
    name: '',
    description: '',
    imageUrl: '',
    location: '',
    price: '',
    condition: 'New'
  });

  // Save items to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('vendorItems', JSON.stringify(items));
  }, [items]);

  const handleNewItemChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imageUrl' && files && files[0]) {
      const imageURL = URL.createObjectURL(files[0]);
      setNewItem({ ...newItem, imageUrl: imageURL });
    } else {
      setNewItem({ ...newItem, [name]: value });
    }
  };

  const handleNewItemSubmit = (e) => {
    e.preventDefault();
    if (items.length >= 20) {
      alert("Maximum of 20 items reached. You cannot add more items.");
      return;
    }
    // Create a new item with a unique id and assign a sequential inventory number
    const itemToAdd = {
      ...newItem,
      id: Date.now(),
      shop: vendorStoreName,
      inventoryNumber: items.length + 1
    };
    setItems([...items, itemToAdd]);
    setNewItem({ name: '', description: '', imageUrl: '', location: newItem.location, price: '', condition: 'New' });
  };

  const handleEditClick = (item) => {
    setEditingItemId(item.id);
    setEditingItemData({
      name: item.name,
      description: item.description,
      imageUrl: item.imageUrl,
      location: item.location,
      price: item.price,
      condition: item.condition
    });
  };

  const handleEditingItemChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imageUrl' && files && files[0]) {
      const imageURL = URL.createObjectURL(files[0]);
      setEditingItemData({ ...editingItemData, imageUrl: imageURL });
    } else {
      setEditingItemData({ ...editingItemData, [name]: value });
    }
  };

  const handleEditSubmit = (e, itemId) => {
    e.preventDefault();
    const updatedItems = items.map(item => {
      if (item.id === itemId) {
        return { ...item, ...editingItemData };
      }
      return item;
    });
    setItems(updatedItems);
    setEditingItemId(null);
  };

  const handleEditCancel = () => {
    setEditingItemId(null);
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ marginBottom: '0.5rem', color: '#333' }}>Vendor Portal</h2>
      <h3 style={{ marginBottom: '2rem', color: '#555' }}>Welcome, {vendorStoreName}</h3>

      {/* Your Items Section in a Grid Layout */}
      <section style={{ marginBottom: '2rem', padding: '1rem', border: '1px solid #eee', borderRadius: '8px', background: '#fafafa' }}>
        <h4 style={{ marginBottom: '1rem', color: '#444' }}>Your Items</h4>
        {items.length === 0 ? (
          <p style={{ color: '#777' }}>No items listed yet.</p>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1rem' }}>
            {items.map(item => (
              <div key={item.id} style={{ border: '1px solid #ddd', padding: '1rem', borderRadius: '8px', background: '#fff' }}>
                {editingItemId === item.id ? (
                  <form onSubmit={(e) => handleEditSubmit(e, item.id)} style={{ textAlign: 'left' }}>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <label style={{ fontWeight: 'bold' }}>Item Name:</label><br />
                      <input
                        type="text"
                        name="name"
                        value={editingItemData.name}
                        onChange={handleEditingItemChange}
                        required
                        style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
                      />
                    </div>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <label style={{ fontWeight: 'bold' }}>Description:</label><br />
                      <textarea
                        name="description"
                        value={editingItemData.description}
                        onChange={handleEditingItemChange}
                        required
                        style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
                      />
                    </div>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <label style={{ fontWeight: 'bold' }}>Price:</label><br />
                      <input
                        type="text"
                        name="price"
                        value={editingItemData.price}
                        onChange={handleEditingItemChange}
                        required
                        style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
                      />
                    </div>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <label style={{ fontWeight: 'bold' }}>Condition:</label><br />
                      <select
                        name="condition"
                        value={editingItemData.condition}
                        onChange={handleEditingItemChange}
                        required
                        style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
                      >
                        <option value="New">New</option>
                        <option value="Used">Used</option>
                        <option value="Refurbished">Refurbished</option>
                      </select>
                    </div>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <label style={{ fontWeight: 'bold' }}>Upload New Image:</label><br />
                      <input
                        type="file"
                        name="imageUrl"
                        accept="image/*"
                        onChange={handleEditingItemChange}
                        style={{ width: '100%' }}
                      />
                    </div>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <label style={{ fontWeight: 'bold' }}>Location:</label><br />
                      <input
                        type="text"
                        name="location"
                        value={editingItemData.location}
                        onChange={handleEditingItemChange}
                        required
                        style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
                      />
                    </div>
                    <div style={{ marginTop: '0.5rem' }}>
                      <button type="submit" style={{ marginRight: '1rem', padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                        Save
                      </button>
                      <button type="button" onClick={handleEditCancel} style={{ padding: '0.5rem 1rem', backgroundColor: '#ccc', color: '#333', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                        Cancel
                      </button>
                    </div>
                  </form>
                ) : (
                  <>
                    <h4 style={{ margin: '0.5rem 0', color: '#333' }}>
                      {item.inventoryNumber}. {item.name}
                    </h4>
                    <p style={{ color: '#555' }}>{item.description}</p>
                    <p style={{ color: '#555' }}><strong>Price:</strong> {item.price}</p>
                    <p style={{ color: '#555' }}><strong>Condition:</strong> {item.condition}</p>
                    {item.imageUrl && (
                      <img
                        src={item.imageUrl}
                        alt={item.name}
                        style={{ width: '100%', maxWidth: '280px', borderRadius: '4px', margin: '0.5rem 0' }}
                      />
                    )}
                    <p style={{ color: '#555' }}><strong>Location:</strong> {item.location}</p>
                    <button
                      onClick={() => handleEditClick(item)}
                      style={{ marginTop: '0.5rem', padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                    >
                      Edit
                    </button>
                  </>
                )}
              </div>
            ))}
          </div>
        )}
      </section>

      <h3 style={{ color: '#333' }}>Add a New Item</h3>
      {items.length < 20 ? (
        <form onSubmit={handleNewItemSubmit} style={{ display: 'inline-block', textAlign: 'left', marginBottom: '2rem', width: '100%', maxWidth: '500px' }}>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ fontWeight: 'bold' }}>Item Name:</label><br />
            <input 
              type="text" 
              name="name" 
              value={newItem.name} 
              onChange={handleNewItemChange} 
              required
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ fontWeight: 'bold' }}>Description:</label><br />
            <textarea 
              name="description" 
              value={newItem.description} 
              onChange={handleNewItemChange} 
              required
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ fontWeight: 'bold' }}>Price:</label><br />
            <input 
              type="text" 
              name="price" 
              value={newItem.price} 
              onChange={handleNewItemChange} 
              required
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ fontWeight: 'bold' }}>Condition:</label><br />
            <select
              name="condition"
              value={newItem.condition}
              onChange={handleNewItemChange}
              required
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
            >
              <option value="New">New</option>
              <option value="Used">Used</option>
              <option value="Refurbished">Refurbished</option>
            </select>
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ fontWeight: 'bold' }}>Upload Image:</label><br />
            <input 
              type="file" 
              name="imageUrl" 
              accept="image/*"
              onChange={handleNewItemChange} 
              required
              style={{ width: '100%' }}
            />
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ fontWeight: 'bold' }}>Location:</label><br />
            <input 
              type="text" 
              name="location" 
              value={newItem.location} 
              onChange={handleNewItemChange} 
              required
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' }}
            />
          </div>
          <div style={{ textAlign: 'center' }}>
            <button 
              type="submit" 
              style={{ padding: '0.5rem 1rem', backgroundColor: '#ffa24d', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Add Item
            </button>
          </div>
        </form>
      ) : (
        <p style={{ color: '#777' }}>You have reached the maximum of 20 items.</p>
      )}
    </div>
  );
};

export default VendorPortal;
